class LoginPage {

  constructor(){
    this.url = '/';
    this.name = '#name';
    this.password = '#password';
    this.submitButton = '#button_primary';
  }

  visit(){
    return cy.visit(this.url);
  }
  getUrl(){
    return this.url;
  }
  setName(name){
    const field = cy.get(this.name);
    field.should('be.visible');
    field.clear();
    field.type(name);
    return this;
  }
  setPassword(password){
    const field = cy.get(this.password);
    field.should('be.visible');
    field.clear();
    field.type(password);
    return this;
  }

  submit(){
    const button = cy.get(this.submitButton);
    button.should('be.visible');
    button.click();
    return this;

  }

  login(name,password){
    this.setName(name);
    this.setPassword(password);
    this.submit();
    return this
  }

}

module.exports = LoginPage;
